INSERT INTO `introduce` VALUES (1,'Giới thiệu về chúng tôi','Doanh nghiệp Than Phương Anh với phương châm hoạt động An toàn','','1. Giới thiệu về chúng tôi','<p>Doanh nghiệp Than Phương Anh với phương ch&acirc;m hoạt động An to&agrave;n - Hiệu quả- Ph&aacute;t triển vững mạnh. Ti&ecirc;u ch&iacute; Bảo vệ sức khỏe cho người ti&ecirc;u d&ugrave;ng</p>\r\n\r\n<p>Ch&iacute;nh v&igrave; vậy, l&agrave; 1 trong những doanh nghiệp đi đầu trong việc cung ứng c&aacute;c sản phẩm than củi chất lượng cao trong nước v&agrave; xuất khẩu. Sản phẩm của ch&uacute;ng t&ocirc;i lu&ocirc;n lu&ocirc;n sử dụng nguy&ecirc;n liệu tự nhi&ecirc;n, kh&ocirc;ng chất độc hại, đảm bảo mang đến cho người ti&ecirc;u d&ugrave;ng c&aacute;c sản phẩm than sạch, th&acirc;n thiện m&ocirc;i trường.</p>\r\n\r\n<p>Hiện tại, doanh nghiệp Than Phương Anh ch&uacute;ng t&ocirc;i chuy&ecirc;n cung cấp c&aacute;c loại than: Than tổ ong; Than củi ( than đước, than cừ, than nh&atilde;n); Than cục; than sạch; Than c&aacute;m; Bếp than; Bếp than tổ ong...</p>\r\n\r\n<p>Với mục ti&ecirc;u: n&acirc;ng cao chất lượng cuộc sống, đảm bảo an to&agrave;n sức khỏe, v&agrave; sự h&agrave;i l&ograve;ng của người ti&ecirc;u d&ugrave;ng. Doanh nghiệp Phương anh kh&ocirc;ng ngừng nỗ lực phấn đấu mở rộng quy m&ocirc; cũng như n&acirc;ng cao chất lượng sản phẩm, đa dạng h&oacute;a sản phẩm để đ&aacute;p ứng cho thị trường trong nước cũng như c&aacute;c thị trường y&ecirc;u cầu cao về ti&ecirc;u chuẩn chất lượng rất kh&oacute; t&iacute;nh giống như Nhật, Mỹ, H&agrave;n, c&aacute;c nước Ch&acirc;u &Acirc;u, .... v&agrave; nhiều thị trường tiềm năng như Trung Đ&ocirc;ng, Nam A, c&aacute;c nước trong khu vực Đ&ocirc;ng Nam &Aacute;.....</p>\r\n\r\n<p>Doanh nghiệp Than Phương Anh với đội ngũ nh&acirc;n vi&ecirc;n trẻ, năng động, gi&agrave;u nhiệt huyết, được đ&agrave;o tạo hướng dẫn chuy&ecirc;n nghiệp. Ch&uacute;ng t&ocirc;i cam kết đem đến cho người ti&ecirc;u d&ugrave;ng d&ograve;ng sản phẩm chất lượng, gi&aacute; cả cạnh tranh v&agrave; sự phục vụ chu đ&aacute;o tốt nhất.</p>\r\n\r\n<p>H&atilde;y li&ecirc;n hệ ngay với ch&uacute;ng t&ocirc;i để nhận được , sản phẩm chất lượng , đảm bảo, sự phục vụ tốt nhất của Phương Anh!</p>\r\n\r\n<p>DOANH NGHIỆP THAN PHƯƠNG ANH</p>\r\n\r\n<p>Đường V&otilde; Văn V&acirc;n, ấp 4, Vĩnh Lộc B, B&igrave;nh Ch&aacute;nh, Tp. Hồ Ch&iacute; Minh.</p>\r\n\r\n<p>(08) 88 08 08 08 - 0909 123 456</p>\r\n\r\n<p>info@thanphuonganh.com</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>2. C&aacute;c sản phẩm đang kinh doanh</p>\r\n\r\n<ul>\r\n	<li>Than tổ ong</li>\r\n	<li>Than Sạch</li>\r\n	<li>Than c&aacute;m</li>\r\n	<li>Than củi Nh&atilde;n</li>\r\n	<li>Than củi Đước</li>\r\n	<li>Than củi X&agrave; Cừ</li>\r\n	<li>Than cục</li>\r\n	<li>Bếp than</li>\r\n	<li>Bếp than tổ ong</li>\r\n</ul>\r\n',1,NULL);
